// Barba Init
	function initBarba() {
		if (window.__barbaInited) return;
  		window.__barbaInited = true;
		barba.init({
			debug: DEBUG,
			transitions: [
			// 1) first-load / preloader
			// {
				// name: "initial-preloader",
				// once: async ({ next }) => {
				// 	if (shouldShowPreloader()) {
				// 		preloader.style.display = "flex";
				// 		await runIntroTimeline();
				// 		await runPreloader();
				// 	}
					
				// 	next();
				// 	initAllYourInits();
				// 	initNavigation(document);
				// }
			// },
				{
					name: "initial-load",
					once: async ({ next }) => {
						await runEntryFlow(next.container);
					    reinitWebflowModules();
						if (!location.hash) window.scrollTo(0, 0);
					}
				},{
					name: "page-fade",
					custom: (data) => {
						const fromPath = normalizePath(data?.current?.url?.path || location.pathname);
						const rawTo = data?.trigger?.getAttribute?.('href') || data?.href || data?.next?.url?.pathname || data?.next?.url?.path || data?.next?.url?.href || "";
						const toPath = normalizePath(rawTo);
						return isWorkPath(fromPath) && isWorkPath(toPath);
					},
					leave: async ({ current }) => {
						saveScroll();
						await gsap.to(current.container, { autoAlpha: 0, duration: 0.5, ease: "power1.out" });
						destroyAllYourInits();
						current.container.remove();
					},
					enter: async ({ next }) => {
						resetWebflow({ next });
						const wasHistory = __historyNav; __historyNav = false;
						if (wasHistory) {
							const pos = readScroll(); if (pos) window.scrollTo(pos.x, pos.y);
						} else if (!location.hash) {
							window.scrollTo(0, 0);
						}
						await runEntryFlow(next.container, { withCoverOut: false });
					},
					afterEnter: ({ next }) => {
						requestAnimationFrame(() => reinitWebflowModules());
						next.container.querySelectorAll("video[autoplay]").forEach(v => { v.muted = true; v.play().catch(()=>{}) });
						setTimeout(() => { initDynamicPortraitColumns(next.container); ScrollTrigger.refresh(true); }, 30);
					}
				},{
					name: "page-swipe",
					leave: async ({ current }) => {
						saveScroll();
						await coverIn();
						destroyAllYourInits();
						current.container.remove();
					},
					enter: async ({ next }) => {
						resetWebflow({ next });
						const wasHistory = __historyNav; __historyNav = false;
						if (wasHistory) {
							const pos = readScroll(); if (pos) window.scrollTo(pos.x, pos.y);
						} else if (!location.hash) {
							window.scrollTo(0, 0);
						}
						await runEntryFlow(next.container, { withCoverOut: true });
					},
					afterEnter({ next }) {
						requestAnimationFrame(() => reinitWebflowModules());
						next.container.querySelectorAll("video[autoplay]").forEach(v => { v.muted = true; v.play().catch(()=>{}) });
						setTimeout(() => { initDynamicPortraitColumns(next.container); ScrollTrigger.refresh(true); }, 30);
					}
				}
			]
		});
	}

// Run All Initialisers
	let _firstLoadDone = false;
	function initAllYourInits(root = document) {
		const ns = root?.dataset?.barbaNamespace || root.getAttribute("data-barba-namespace") || "";

		setActiveTab(ns);
  		applyOverscroll(ns);
		
		initReparentChildren(root);
		initTextAnimationOne(root);
		initAppearInLine(root);
		initNavigation(root);
		initMenuLinkHover(root);
		initCaseStudyCloseButton(root);
		initThemeSwitch(root);
		initAccordions(root);
		
		if (ns === "selected") {
			initSelectedWorkLoop(root);
		}
		if (ns === "archive") {
			initArchiveFilters(root);
		}
		if (ns === "resources") {
			initResourcesPinnedSections(root);
		}
		
		requestAnimationFrame(() => reinitWebflowModules());
		if (destroyCursor) { destroyCursor(); destroyCursor = null; }
		if (window.matchMedia('(pointer:fine)').matches) {
			destroyCursor = initCustomCursor(root);
		}
	}

if(typeof initBarba==="function")window.initBarba=initBarba;
if(typeof initAllYourInits==="function")window.initAllYourInits=initAllYourInits;
