// Barba Init
	function initBarba() {
		if (window.__barbaInited) return;
  		window.__barbaInited = true;
		barba.init({
			debug: true,
			transitions: [
			// 1) first-load / preloader
			// {
				// name: "initial-preloader",
				// once: async ({ next }) => {
				// 	if (shouldShowPreloader()) {
				// 		preloader.style.display = "flex";
				// 		await runIntroTimeline();
				// 		await runPreloader();
				// 	}
					
				// 	next();
				// 	initAllYourInits();
				// 	initNavigation(document);
				// }
			// },
				{
					name: "initial-load",
					once: async ({ next }) => {
						await runEntryFlow(next.container);
					    preselectTabFromURL(next.container);
					    finalizeOpenTabFromURL(next.container);
					    reinitWebflowModules();
					    requestAnimationFrame(() => {
							preselectTabFromURL(next.container);
							finalizeOpenTabFromURL(next.container);
					    });
					    initDynamicPortraitColumns(next.container);
					}
				},
				{
					name: "page-swipe",
					leave: async ({ current }) => {
						_freezeScroll();
					    const tl = coverIn();
					    if (tl?.finished && typeof tl.finished.then === "function") {
					      await tl.finished;
					    } else {
					      await new Promise((resolve) => tl.eventCallback("onComplete", resolve));
					    }
					    destroyAllYourInits();
					    current.container.remove();
					},
					enter: async ({ next }) => {
						resetWebflow({ next });
						await runEntryFlow(next.container, { withCoverOut: true });
						preselectTabFromURL(next.container);
						finalizeOpenTabFromURL(next.container);
					},
					afterEnter({ next }) {
						_unfreezeScroll();
					    requestAnimationFrame(() => reinitWebflowModules());
					    requestAnimationFrame(() => {
					    	preselectTabFromURL(next.container);
					    	finalizeOpenTabFromURL(next.container);
					    });
					    next.container.querySelectorAll("video[autoplay]").forEach(v => {
					    	v.muted = true; v.play().catch(() => {});
						});
					    setTimeout(() => {
					    	initDynamicPortraitColumns(next.container);
					    	ScrollTrigger.refresh(true);
					    }, 30);
					}
				}
			]
		});
	}

// Run All Initialisers
	let _firstLoadDone = false;
	function initAllYourInits(root = document) {
		initReparentChildren(root);
		markTabLinksForBarba(root);
		initTextAnimationOne(root);
		initSelectedWorkLoop(root);
		initArchiveResultsCounter(root);
		initArchiveFilters(root);
		initNavigation(root);
		initMenuLinkHover(root);
		ensureResourcesScroll(root);
		initCaseStudyCloseButton(root);
		initThemeSwitch(root);
		initAccordions(root);
		initAppearInLine(root);
		initOverscrollBehavior(root);
		requestAnimationFrame(() => reinitWebflowModules());
		if (destroyCursor) { destroyCursor(); destroyCursor = null; }
		if (window.matchMedia('(pointer:fine)').matches) {
			destroyCursor = initCustomCursor(root);
		}
	}

if(typeof initBarba==="function")window.initBarba=initBarba;
if(typeof initAllYourInits==="function")window.initAllYourInits=initAllYourInits;
